#include<stdio.h>

int main() {
    int a = 10, b = 5;   // integer variables
    float x = 5.5, y = 2.2; // float variables

    // Arithmetic operations
    printf("Addition: %d\n", a + b);       // integer addition
    printf("Subtraction: %d\n", a - b);    // integer subtraction
    printf("Multiplication: %d\n", a * b); // integer multiplication
    printf("Division: %d\n", a / b);       // integer division
    printf("Float division: %.2f\n", x / y); // float division

    return 0;
}


